import { Fragment, useEffect, useState } from 'react';
import classes from './Throttle.module.css';
import GaugeChart from 'react-gauge-chart';
import './Throttle.scss';

const Throttle = () => {
    const [throttle, setThrottle] = useState(0);
    const colors = Array(30).fill("#FFFFFF")

    const onThrottleHandler = (event) => {
        setThrottle(event.target.value);
        const _R = document.querySelector('input[type=range]');

        document.documentElement.classList.add('js');

        _R.addEventListener('input', e => {
            _R.style.setProperty('--val', +_R.value)
        }, false);
    }



    return (
        <Fragment>
            <div className={classes.backdrop}></div>
            <div className={classes.gauge}>
                <GaugeChart id="speedometer"
                    nrOfLevels={30}
                    cornerRadius={3}
                    percent={throttle / 20}
                    arcPadding={0.021}
                    arcWidth={0.05}
                    colors={colors}
                    formatTextValue={value => ''}
                    className={classes.gauge}
                />
                <p className={classes.gaugeReading}>{throttle} mph</p>
                <p className={classes.odo}>Odometer</p>
                <div className={classes.gaugeFooter}>
                    <div>☁️ &nbsp; 85 &#176; F</div>
                    <div>TRIP: 0.0 mi</div>
                </div>

                <div className={classes.throttle}>
                    <input type="range" min="0" max="18" step="0.125" value={throttle} onChange={onThrottleHandler} />
                </div>
            </div>
        </Fragment>
    );
}

export default Throttle;
