import { Fragment, useRef } from 'react';
import classes from './Credentials.module.css';
import { useAuth } from '../store/auth-context';
import { Redirect } from 'react-router';

const SignUp = () => {
    const { signup } = useAuth();
    const emailRef = useRef();
    const passwordRef = useRef();
    const onSubmitHandler = (e) => {
        e.preventDefault();
        console.log(emailRef.current.value, passwordRef.current.value);
        signup(emailRef.current.value, passwordRef.current.value);
        return <Redirect to='/throttle' />
    }

    const onSignInHandler = () => {
        window.location.reload(false);
    }
    return (
        <Fragment>
            <div className={classes.signup}>
                <div className={classes.header}>
                    <p><b>Create new account</b></p>
                </div>
                <div className={classes.form}>
                    <form>
                        <input className={classes.inputBox} type="text" placeholder="First Name" />
                        <input className={classes.inputBox} type="text" placeholder="Second Name" />
                        <div className={classes.gap}></div>
                        <input className={classes.inputBox} type="email" placeholder="E-mail Address" ref={emailRef} />
                        <div className={classes.gap}></div>
                        <input className={classes.inputBox} type="password" placeholder="Password" ref={passwordRef} />
                        <input className={classes.inputBox} type="password" placeholder="Confirm Password" />
                        <div className={classes.gap}></div>
                        <button className={classes.submit} onClick={onSubmitHandler}><b>SIGN UP</b></button>
                    </form>
                    <div className={classes.footer}>
                        <u onClick={onSignInHandler}>Already have an account?</u>
                    </div>
                </div>
            </div>
        </Fragment>
    );
}

export default SignUp;
